mapper.select:
SELECT f_no, f_memberno, f_content, f_date
FROM feed
WHERE f_no = #{fNo}


SELECT rnum, f_no, f_memberno, f_content, f_date
FROM (
  SELECT rownum AS rnum, f_no, f_memberno, f_content, f_date
  FROM (
    SELECT f_no, f_memberno, f_content, f_date
    FROM feed
    WHERE f_memberno = #{fMemberNo}
    ORDER BY f_date DESC
  )
  WHERE rownum <= #{pager.endRowNo}
)
WHERE rnum >= #{pager.startRowNo}

SELECT f_no, f_memberno, f_content, f_date
FROM feed
WHERE f_memberno = #{fMemberno}

SELECT count(*)
FROM feed
WHERE f_memberno = #{fMemberno}
